var stop, staticx;
var img = new Image();
img.src = "data:image/png;base64,";

function Chicken(x, y, s, r, fn) {
  this.x = x;
  this.y = y;
  this.s = s;
  this.r = r;
  this.fn = fn;
}

Chicken.prototype.draw = function(cxt) {
  cxt.save();
  var xc = 40 * this.s / 4;
  cxt.translate(this.x, this.y);
  cxt.rotate(this.r);
  cxt.drawImage(img, 0, 0, 40 * this.s, 40 * this.s)
  cxt.restore();
}

Chicken.prototype.update = function() {
  this.x = this.fn.x(this.x, this.y);
  this.y = this.fn.y(this.y, this.y);
  this.r = this.fn.r(this.r);
  if(this.x > window.innerWidth ||
    this.x < 0 ||
    this.y > window.innerHeight ||
    this.y < 0
  ) {
    this.r = getRandom('fnr');
    if(Math.random() > 0.4) {
      this.x = getRandom('x');
      this.y = 0;
      this.s = getRandom('s');
      this.r = getRandom('r');
    } else {
      this.x = window.innerWidth;
      this.y = getRandom('y');
      this.s = getRandom('s');
      this.r = getRandom('r');
    }
  }
}

ChickenList = function() {
  this.list = [];
}
ChickenList.prototype.push = function(chicken) {
  this.list.push(chicken);
}
ChickenList.prototype.update = function() {
  for(var i = 0, len = this.list.length; i < len; i++) {
    this.list[i].update();
  }
}
ChickenList.prototype.draw = function(cxt) {
  for(var i = 0, len = this.list.length; i < len; i++) {
    this.list[i].draw(cxt);
  }
}
ChickenList.prototype.get = function(i) {
  return this.list[i];
}
ChickenList.prototype.size = function() {
  return this.list.length;
}

function getRandom(option) {
  var ret, random;
  // 判断是否为移动设备（屏幕宽度小于768px）
  var isMobile = window.innerWidth < 768;
  switch(option) {
    case 'x':
      ret = Math.random() * window.innerWidth;
      break;
    case 'y':
      ret = Math.random() * window.innerHeight;
      break;
    case 's':
      // 手机端：最小0.4，最大0.8；电脑端：最小0.3，最大1.0
      if (isMobile) {
        ret = 0.4 + Math.random() * 0.4; // 0.4 ~ 0.8
      } else {
        ret = 0.3 + Math.random() * 0.7; // 0.3 ~ 1.0
      }
      break;
    case 'r':
      ret = Math.random() * 6;
      break;
    case 'fnx':
      random = -0.5 + Math.random() * 1;
      ret = function(x, y) {
        return x + 0.5 * random - 1.7;
      };
      break;
    case 'fny':
      random = 1.5 + Math.random() * 0.7
      ret = function(x, y) {
        return y + random;
      };
      break;
    case 'fnr':
      random = Math.random() * 0.03;
      ret = function(r) {
        return r + random;
      };
      break;
  }
  return ret;
}

function startChicken() {

  requestAnimationFrame = window.requestAnimationFrame ||
    window.mozRequestAnimationFrame ||
    window.webkitRequestAnimationFrame ||
    window.msRequestAnimationFrame ||
    window.oRequestAnimationFrame;
  var canvas = document.createElement('canvas'),
    cxt;
  staticx = true;

  // 获取设备像素比，解决高分辨率屏幕模糊问题
  var dpr = window.devicePixelRatio || 1;
  var width = window.innerWidth;
  var height = window.innerHeight;

  // 设置canvas实际尺寸（考虑设备像素比）
  canvas.width = width * dpr;
  canvas.height = height * dpr;

  // 设置canvas显示尺寸
  canvas.setAttribute('style', 'position: fixed;left: 0;top: 0;pointer-events: none;width:' + width + 'px;height:' + height + 'px;');
  canvas.setAttribute('id', 'canvas_chicken');
  document.getElementsByTagName('body')[0].appendChild(canvas);
  cxt = canvas.getContext('2d');

  // 缩放context以匹配设备像素比
  cxt.scale(dpr, dpr);

  var chickenList = new ChickenList();

  // 根据屏幕大小动态计算图标数量
  // 手机端(宽度<768)：15-20个，电脑端：35-50个
  var isMobile = window.innerWidth < 768;
  var chickenCount = isMobile ? Math.floor(15 + Math.random() * 5) : Math.floor(35 + Math.random() * 15);

  for(var i = 0; i < chickenCount; i++) {
    var chicken, randomX, randomY, randomS, randomR, randomFnx, randomFny;
    randomX = getRandom('x');
    randomY = getRandom('y');
    randomR = getRandom('r');
    randomS = getRandom('s');
    randomFnx = getRandom('fnx');
    randomFny = getRandom('fny');
    randomFnR = getRandom('fnr');
    chicken = new Chicken(randomX, randomY, randomS, randomR, {
      x: randomFnx,
      y: randomFny,
      r: randomFnR
    });
    chicken.draw(cxt);
    chickenList.push(chicken);
  }
  stop = requestAnimationFrame(function() {
    cxt.clearRect(0, 0, canvas.width, canvas.height);
    chickenList.update();
    chickenList.draw(cxt);
    stop = requestAnimationFrame(arguments.callee);
  })
}

window.onresize = function() {
  var canvasSnow = document.getElementById('canvas_snow');
  canvasSnow.width = window.innerWidth;
  canvasSnow.height = window.innerHeight;
}

img.onload = function() {
  startChicken();
}

function stopp() {
  if(staticx) {
    var child = document.getElementById("canvas_chicken");
    child.parentNode.removeChild(child);
    window.cancelAnimationFrame(stop);
    staticx = false;
  } else {
    startChicken();
  }
}
